import React from 'react';
import './App.css';
import Game from './components/Game';

const App: React.FC = () => {
  // Default to medium difficulty
  return (
    <div className="App">
      <header className="App-header">
        <h1>Minesweeper</h1>
      </header>
      <main>
        <Game initialRows={16} initialCols={16} initialMines={40} />
      </main>
    </div>
  );
};

export default App;
